@css:#pb_content .d_name,#pb_content .tail-info,#pb_content .p_content@html
<js>result=String(result).replace(/\s*该楼层疑似违[\s\S]+?查看此楼|<img.*?class="nicknameEmoji".*?>/g,'').replace(/\s*<span class="tail-info">\s*/g,'—').replace(/<li class="d_name">[\s\S]+?ad-dom-img="true">/g,'').replace(/<img.*?ad-dom-img="true">/g,'').replace(/<img class="icon-jubao".*?>|—来自.*?端|手机贴吧|.*?快来下载吧！.*?>/g,'');
if(!baseUrl.match(/see_lz/)){
result=result.replace(/<li.*?class="p_author_name.*>([^<]+)<\/a>\s*<\/li>/g,'📃$1📃')}else{
result=org.jsoup.Jsoup.parse(result).select('.p_content').html();
String(book.customIntro).match(/去空行/)?result=result:
result=String(result).replace(/<a rel="noopener" class="save_face_card"><\/a> /g,'<br>&lrm;<br>')
}</js>
<js>
String(book.customIntro).match(/去空行/)?result=result:result.replace(/(—.*?—\d+-\d+-\d+ \d+:\d+)/g,'$1\n&zwnj;\n')</js>
