var doc = org.jsoup.Jsoup.parse(result)
if(baseUrl.match(/sch/)){
var title = doc.select(".list_content h2 a");
var desc = doc.select(".list_content table[width=80%]");
var list = [];
for(var i=0;i<title.size();i++){
 list[i] = title.get(i)+'<a>'+desc.get(i)+'</a>'
}
result = list
}else{
var img = doc.select(".down_list td[rowspan=3]");
var title = doc.select(".down_list td[width=290]");
var info = doc.select(".down_list td[align=center]");
var desc = doc.select(".down_list td[colspan=5] b");
var detail = []
for(var i=0;i<img.size();i++){
detail[i] = '<a>'+info.get(i*3)+' Dl: '+info.get(i*3+2)+' Size: '+info.get(i*3+1)+'</a>'+'<a>'+desc.get(i)+'</a>'
}
var list = [];
for(var i=0;i<img.size();i++){
 list[i] = img.get(i)+title.get(i)+detail[i]
}
result = list}

