@js:
var title = "{{@@h1@text}}";
var detail = java.getElements("id.mainSoftIntro").text();
var img = java.getElements("id.downInfoArea@tag.img").select("img").get(1).attr("src")
var imgSrc = "https://www.iysw.net/"+img;
var bookInfo ="{{@@.downInfoRowR@text}}";
var author = bookInfo.match(/作者.*类别/)[0].replace(/文章类别/,"");
var type = bookInfo.match(/类型：.*文件/)[0].replace(/(文件|\.)/g,"");
var dlUrl = "https://www.iysw.net"+"{{@@id.downAddress@tag.a.0@onclick}}".match(/\/.*id=/)+0;
var doc = org.jsoup.Jsoup.connect(dlUrl).get();
var fileUrl_1 = "https://www.iysw.net/e/DownSys"+doc.select("a").get(1).attr("href").match(/\/.*/);
var fileUrl_2 = String(fileUrl_1).replace(/pathid=0/,"pathid=1");
var tips = '<a id = "tips">txt文本文件请复制链接用下载器下载(注意后面3个:::)，7z, zip, rar压缩文件点击直接下载</a>'

result = '<h5>'+title+'</h5>'+'<img src = '+imgSrc+'><p>'+author+'<p>'+type+'<p>简介：'+detail+'<hr>'+tips+'<p><hr><a id = "dl" href = '+fileUrl_1+' >下载地址1</a><p><a id = "link">'+fileUrl_1+'<hr></a><a id = "dl" href = '+fileUrl_2+' >下载地址2</a><p><a id = "link">'+fileUrl_2+'</a><hr>'

