    var html = htmls[i].body();
    var title = {
        name: html.replace(" ", '').substring(html.replace(" ", '').indexOf('view-fix-top-bar-title') + 24, html.replace(" ", '').indexOf('view-fix-top-bar-right') - 16),
        imgs: []
    };
    html = html.substr(html.indexOf('cp_img">') + 8);
    html = html.substr(0, html.indexOf('</div'));
    var imgds = html.replace(/ /g, '').split('\n');
    for (var j = 0; j < imgds.length; j++) {
        if (imgds[j].indexOf('img') != -1) {
            title.imgs.push(imgds[j].substring(imgds[j].indexOf('data-original="') + 15, imgds[j].indexOf('"src="')));
        }
    }
    chapts.push(title);
}

result=chapts.map(c => "<h1>" + c.name + "</h1>" + c.imgs.map(d => "<img src='" + d + "'/>").join('')).join('');

